package loops;

public class DoWhilePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =100; //initialization
		
		do {
			System.out.println(i+"x 5 = "+i*5);    //loop body
			i--; //decrement
			
		} while(i>=1); //Condition

	}

}
