package loops;

public class IfLadder {
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 4;
		
		if (a>=5)  //Condition
		{
			System.out.println("The entered number is either greater than or equal to 5");
		}
		
		else {
			System.out.println("The entered number is less than 5");
		}

	}

}
